import React, { useEffect } from "react";
import { Media, Button } from "react-bootstrap";
import { useLocation } from "react-router-dom";

const HomePage = (props) => {
  let location = useLocation();

  useEffect(
    () => console.log("page rendered", location.pathname.split("/ilan/")),
    []
  );
  // useEffect(() => console.log("page rendered", props.products), []);
  // get products as props, filter relevant product
  const product = props.products.filter(
    (product) => product.title === location.pathname.split("/ilan/")[1]
  )[0];

  useEffect(() => console.log("product:", product));

  return (
    <Media>
      <img
        width={512}
        height={512}
        className="mr-3"
        src={product.img}
        alt="hello"
      />
      <Media.Body>
        <h5>{product.title}</h5>
        <br />
        <br />
        <br />

        <p>{product.about}</p>
        <p className="mark">
          <strong>
            Price:
            {product.price}
          </strong>
        </p>
        <Button variant="warning" href={product.link}>
          Buy It On Amazon
        </Button>
      </Media.Body>
    </Media>
  );
};

export default Page;
